import "./App.css";
import { useState, useEffect } from "react";
import Book from "./Components/Book";
import { get, getAll, update, search } from "./BooksAPI";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [allBooks, setAllBooks] = useState([]);

  const [searchBooks, setSearchBooks] = useState([]);

  useEffect(() => {
    getAll().then((data) => {
      setAllBooks(data);
    });
  }, []);

  const moveToShelf = async (book, targetShelf) => {
    // console.log("book", book);
    // console.log("targetShelf", targetShelf);
    const response = await update(book, targetShelf);
    // console.log(response);
    setAllBooks((books) => {
      const newBooks = books.map((b) => {
        if (b.id !== book.id) {
          return b;
        } else {
          return {
            ...b,
            shelf: targetShelf,
          };
        }
      });

      if (!newBooks.find((existingBook) => existingBook.id === book.id)) {
        newBooks.push(book);
      }
      return newBooks;
    });
  };

  const handleSearch = (e) => {
    const userInput = e.target.value;
    console.log(userInput);
    if (userInput) {
      search(userInput).then((searchResults) => {
        if (searchResults instanceof Array) {
          console.log(searchResults);
          setSearchBooks(
            searchResults.map((searchResultBook) => {
              const existingBook = allBooks.find(
                (book) => searchResultBook.id === book.id
              );
              if (existingBook) {
                searchResultBook.shelf = existingBook.shelf;
              }
              return searchResultBook;
            })
          );
        } else {
          setSearchBooks([]);
        }
      });
    } else {
      setSearchBooks([]);
    }
  };

  // const [currentlyReading, setCurrentlyReading] = useState([]);
  // const [wantToRead, setWantToRead] = useState([]);
  // const [read, setRead] = useState([]);

  const currentlyReading = allBooks.filter((book) => {
    return book.shelf === "currentlyReading";
  });

  const wantToRead = allBooks.filter((book) => {
    return book.shelf === "wantToRead";
  });

  const read = allBooks.filter((book) => {
    return book.shelf === "read";
  });

  // console.log(currentlyReading, wantToRead, read);

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                onChange={handleSearch}
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
              {searchBooks &&
                searchBooks.map((book) => (
                  <li key={book.id}>
                    <Book moveToShelf={moveToShelf} book={book} />
                  </li>
                ))}
            </ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Currently Reading</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                    {currentlyReading.map((book) => (
                      <li key={book.id}>
                        <Book moveToShelf={moveToShelf} book={book} />
                      </li>
                    ))}
                  </ol>
                </div>
              </div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Want to Read</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                    {wantToRead.map((book) => (
                      <li key={book.id}>
                        <Book moveToShelf={moveToShelf} book={book} />
                      </li>
                    ))}
                  </ol>
                </div>
              </div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Read</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                    {read.map((book) => (
                      <li key={book.id}>
                        <Book moveToShelf={moveToShelf} book={book} />
                      </li>
                    ))}
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
